mapper.update:
UPDATE aftersales 
SET    status = 1
WHERE  aftersales_no = #{aftersalesNo}

mapper.select:
SELECT aftersales_no, goods_no, member_no, attach_file, content, password, status, regdate FROM (SELECT CEIL(rownum / 5) request_page, aftersales_no, goods_no, member_no, attach_file, content, password, status, regdate FROM (SELECT aftersales_no, goods_no, member_no, attach_file, content, password, status, TO_CHAR(regdate, 'YYYY-MM-DD HH24:MI') regdate FROM aftersales
WHERE member_no = #{value}
ORDER BY regdate DESC)) WHERE request_page = #{page}
